import "./Navigation.css";
import { useNavigate } from "react-router-dom";

function Navigation({ children, isOpen, onClose }) {
  const navigate = useNavigate();
  function routeToMain() {
    navigate("/");
    onClose();
  }
  function routeToNews() {
    navigate("/saved-news");
    onClose();
  }
  return (
    <nav className={`navigation ${isOpen && "navigation_clicked"}`}>
      <button className="navigation__page-button" onClick={routeToMain}>
        Home
      </button>
      <button className="navigation__page-button" onClick={routeToNews}>
        Saved articles
      </button>
      {children}
    </nav>
  );
}

export default Navigation;
