import { useNavigate } from "react-router-dom";
import "./Header.css";

function Header({ children, theme, isOpen, openPopup, hidden }) {
  const navigate = useNavigate();
  function handleClick() {
    isOpen ? openPopup(false) : openPopup(true);
  }
  return (
    <header
      className={`header ${
        isOpen && (theme ? "header_theme_dark" : "header_theme_light")
      } ${!theme && "header_text_dark"} ${hidden && "header_hidden"}`}
    >
      <span className="header__logo" onClick={() => navigate("/")}>
        NewsExplorer
      </span>
      {children}
      <button
        onClick={handleClick}
        className={`header__menu-button ${
          isOpen && "header__menu-button_cross"
        }`}
        type="button"
      ></button>
    </header>
  );
}

export default Header;
